// This module is set up to be dependency-less so that Narwhal
// will be able to run it right away.


SC.ready(function() {
    var embed = tiki.require("embedded");
    var nodes = document.querySelectorAll(".bespin");
    for (var i = 0; i < nodes.length; i++) {
        var node = nodes[i];
        var options = node.getAttribute('data-bespinoptions');
        node.bespin = embed.useBespin(node, JSON.parse(options));
    }

    // If users want a custom startup
    if (window.onBespinLoad) {
        window.onBespinLoad();
    }
}
);

// Let SC know when the page is loaded
document.addEventListener("DOMContentLoaded", SC.didLoad, false);

// Set up any globals that should leak out of our closure
window.tiki = tiki;
